CREATE TABLE `sbi_steem_ops`.`member_hist` ( `block_num` INT NOT NULL , `block_id`  varchar(40) NOT NULL,  `trx_id`  varchar(40) NOT NULL ,  `trx_num`  INT NOT NULL, `op_num` INT NOT NULL, `timestamp` DATETIME NOT NULL, `type` varchar(30) NOT NULL, `author` varchar(16) DEFAULT NULL, `permlink` varchar(256) DEFAULT NULL, `parent_author` varchar(16) DEFAULT NULL, `parent_permlink` varchar(256) DEFAULT NULL, `voter` varchar(16) DEFAULT NULL, `weight` INT DEFAULT NULL,  PRIMARY KEY (`block_num`, `trx_id`, `op_num`)) ENGINE = InnoDB; 



CREATE TABLE `sbi_steem_ops`.`transfers` ( `virtual_op` INT NOT NULL , `op_acc_index` INT NOT NULL , `op_acc_name` VARCHAR(50) NOT NULL , `block` INT NOT NULL , `trx_in_block` INT NOT NULL , `op_in_trx` INT NOT NULL , `timestamp` DATETIME NOT NULL , `from` VARCHAR(50) NOT NULL, `to` VARCHAR(50) NOT NULL, `amount` decimal(15,6) DEFAULT NULL, `amount_symbol` varchar(5) DEFAULT NULL,  `memo` varchar(2048) DEFAULT NULL, `op_type` varchar(50) NOT NULL, PRIMARY KEY (`op_acc_index`, `op_acc_name`)) ENGINE = InnoDB;

CREATE TABLE `sbi`.`trx` ( `index` INT, `source` VARCHAR(50) NOT NULL, `memo` text, `account` VARCHAR(50) DEFAULT NULL, `sponsor` VARCHAR(50) DEFAULT NULL ,  `sponsee` text, `shares` INT, `vests` decimal(15,6) DEFAULT NULL, `timestamp` DATETIME NOT NULL ,  `status` VARCHAR(50) NOT NULL, `share_type` varchar(50) NOT NULL, PRIMARY KEY (`index`, `source`)) ENGINE = InnoDB;
CREATE TABLE `sbi`.`member` (`account` VARCHAR(50) NOT NULL, `note` text, `shares` INT NOT NULL, `bonus_shares` INT NOT NULL, `total_share_days` INT,  `avg_share_age` float,  `last_comment` DATETIME, `last_post` DATETIME,  `original_enrollment` DATETIME, `latest_enrollment` DATETIME, `flags` text, `earned_rshares` INT, `rewarded_rshares` INT, `balance_rshares` INT,  `upvote_delay` float, `comment_upvote` bool, PRIMARY KEY (`account`)) ENGINE = InnoDB;
CREATE TABLE `sbi`.`transaction_memo` ( `index` INT, `sender` VARCHAR(16) NOT NULL, `to` VARCHAR(16) DEFAULT NULL, `memo` text, `encrypted` BOOLEAN DEFAULT FALSE, `referenced_accounts` text, `amount` decimal(15,6) DEFAULT NULL, `amount_symbol` varchar(5) DEFAULT NULL, `timestamp` DATETIME NOT NULL ,  `id` INTEGER NOT NULL AUTO_INCREMENT, PRIMARY KEY (`id`)) ENGINE = InnoDB;

CREATE TABLE `sbi`.`transaction_out` ( `index` INT, `sender` VARCHAR(16) NOT NULL, `to` VARCHAR(16) DEFAULT NULL, `memo` text, `encrypted` BOOLEAN DEFAULT FALSE, `referenced_accounts` text, `amount` decimal(15,6) DEFAULT NULL, `amount_symbol` varchar(5) DEFAULT NULL, `timestamp` DATETIME NOT NULL ,  `id` INTEGER NOT NULL AUTO_INCREMENT, PRIMARY KEY (`id`)) ENGINE = InnoDB;

CREATE TABLE `sbi`.`pending_refunds` ( `index` INT, `sender` VARCHAR(16) NOT NULL, `to` VARCHAR(16) DEFAULT NULL, `memo` text, `encrypted` BOOLEAN DEFAULT FALSE, `referenced_accounts` text, `amount` decimal(15,6) DEFAULT NULL, `amount_symbol` varchar(5) DEFAULT NULL, `timestamp` DATETIME NOT NULL ,  `id` INTEGER NOT NULL AUTO_INCREMENT, PRIMARY KEY (`id`)) ENGINE = InnoDB;

ALTER TABLE `member` ADD `post_hist_28` INT NULL AFTER `comment_upvote`, ADD `post_hist_7` INT NULL AFTER `post_hist_28`, ADD `norm_post_hist_7` FLOAT NULL AFTER `post_hist_7`, ADD `upvote_count_28` INT NULL AFTER `norm_post_hist_7`, ADD `upvote_weight_28` FLOAT NULL AFTER `upvote_count_28`;

ALTER TABLE `member` ADD `updated_at` DATETIME NOT NULL DEFAULT '1970-01-01 00:00:00' AFTER `upvote_weight_28`;

CREATE TABLE `sbi`.`keys` (`key_type` VARCHAR(50) NOT NULL, `account` VARCHAR(50) NOT NULL, `wif` VARCHAR(50) NOT NULL,  PRIMARY KEY (`account`, `key_type`)) ENGINE = InnoDB;
CREATE TABLE `sbi`.`configuration` (`id` enum('1') NOT NULL, `share_cycle_min` float NOT NULL, `sp_share_ratio` float NOT NULL, `rshares_per_cycle` INT,  `comment_vote_divider` float,  `comment_vote_timeout_h` float, `last_cycle` DATETIME,  PRIMARY KEY (`id`)) ENGINE = InnoDB;
